package com.ahmetkocu.pdfrendereview;import android.graphics.Bitmap;import android.graphics.pdf.PdfRenderer;import android.os.ParcelFileDescriptor;import java.io.IOException;public class MyPdfRenderer {    private static final Object lock = new Object();    PdfRenderer pdfRenderer;    public MyPdfRenderer(ParcelFileDescriptor fileDescriptor) {        try {            pdfRenderer = new PdfRenderer(fileDescriptor);        } catch (IOException e) {            e.printStackTrace();        }    }    public PdfRenderer.Page openPage(int pageIndex) {        if (pdfRenderer != null) {            return pdfRenderer.openPage(pageIndex);        }        return null;    }    public PdfRenderer.Page renderPage(int index, Bitmap bitmap) {        synchronized (lock) {            PdfRenderer.Page page = pdfRenderer.openPage(index);            page.render(bitmap, null, null, PdfRenderer.Page.RENDER_MODE_FOR_DISPLAY);            return page;        }    }    public int getPageCount() {        synchronized (lock) {            return pdfRenderer.getPageCount();        }    }    public void close() {        synchronized (lock) {            try {                pdfRenderer.close();            }            catch (Exception ex) {            }        }    }}